import React, { useState } from 'react'
import PropTypes from 'prop-types'


const Add = ({onAdd}) => {
  const { register, handleSubmit, watch, errors } = useForm();
  const onSubmit = data => console.log(data);
  const[valueInput, setValueInput]= useState({});
  const onHandleChange=(e)=>{
    const { name, value } = e.target;
    setValueInput({
        ...valueInput,
          [name]:value
      });
  }
  const onHandleSubmit =(e) =>{
      e.preventDefault();
      onAdd(valueInput)
  }


    return (
        <div className="card shadow mb-4">
        <div className="card-header py-3">
          <h6 className="m-0 font-weight-bold text-primary">
           Add Product
          </h6>
        </div>
        <div className="card-body">
        <form onSubmit={onHandleSubmit}>
        <div className="form-group">
          <label htmlFor="inputProductName">Product Name</label>
          <input type="text" className="form-control" id="inputProductName" name="name" onChange={onHandleChange}  />
        </div>
        <div className="form-group">
          <label htmlFor="inputProductPrice">Image</label>
          <input type="text" className="form-control" id="inputProductPrice"  name="image" onChange={onHandleChange}   />
        </div>
        <div className="form-group">
          <label htmlFor="inputProductPrice">Product Price</label>
          <input type="text" className="form-control" id="inputProductPrice"  name="price" onChange={onHandleChange}   />
        </div>
        
        <button type="submit" className="btn btn-primary">Submit</button>
      </form>
        </div>
      </div>
    )
}

Add.propTypes = {

}

export default Add
