import React from 'react'
import PropTypes from 'prop-types'
import {useForm} from 'react-hook-form'
import {useHistory, useParams} from 'react-router-dom'
import firebase from './../../../../../firebase'

const EditProduct =  ({onUpdate}) => {
    // validate và lấy dữ liệu từ form 
    const { register, handleSubmit, errors } = useForm();
    let history =  useHistory()
    let {id} =  useParams()
    // tạo sự kiện cho submit để lấy dữ liệu, dữ liệu được đẩy vào data
    const onHandleSubmit = (data) => {
        console.log(data)
    }   
    return (
        <div>
            <form onSubmit={handleSubmit(onHandleSubmit)}>
                <div className="form-group">
                    <label htmlFor="exampleInputEmail1">Name</label>
                    <input type="text" name="name" className="form-control" ref={register} aria-describedby="emailHelp"  placeholder="Enter email" />
                </div>
                <div className="form-group">
                    <label htmlFor="exampleInputPassword1">Image</label>
                    <input type="file" className="form-control" ref={register} name="image" />
                </div>
                <div className="form-group">
                    <label htmlFor="exampleInputPassword1">Price</label>
                    <input type="number" className="form-control" ref={register} name="price" placeholder="Price" />
                </div>
                <button type="submit" className="btn btn-primary">Submit</button>
            </form>
        </div>
    )
}

EditProduct.propTypes = {

}

export default EditProduct
